function startDelayedPageRefesh()
{
    if (activateStatus == 1) {
        var delay = 60000 + randomNumber(0,10000);
        //GM_log("Refresh page after: " + delay/1000 );
        //setTimeout(function(){ location.reload(true) }, delay );
        attack();
    }
}

function addCSS(css){
    var head = document.getElementsByTagName('head')[0];
    var s = document.createElement('style');
    s.setAttribute('type', 'text/css');
    if (s.styleSheet) {
        // IE
        s.styleSheet.cssText = css;
    } else
    {
        // the world
        s.appendChild(document.createTextNode(css));
    }
    head.appendChild(s);
}

function onCheckBoxClick()
{
    if (checkBox.checked)
    {
        GM_log("on");
        activateStatus = 1;
        saveValue("activateStatus", 1);
        startDelayedPageRefesh();
    }
    else
    {
        GM_log("off");
        activateStatus = 0;
        saveValue("activateStatus", 0);
        audio.pause();
        audio.currentTime = 0;
    }

    //location.reload(true);
}

function on() {
    GM_log("on");
    activateStatus = 1;
    saveValue("activateStatus", activateStatus);
    attack();
    //location.reload(true);
}

function off() {
    GM_log("off");
    activateStatus = 0;
    saveValue("activateStatus", activateStatus);
    audio.stop();
    //location.reload(true);
}

function attack() {
    if (activateStatus == 0)
    {
        t = setTimeout(attack, 4000);
        return;
    }
    var allHTMLTags = new Array();
    allHTMLTags = document.getElementsByTagName("*");

    GM_log("Check for Attack");
    for (var i = 0; i < allHTMLTags.length; i++) {
        var cn = allHTMLTags[i].className
        if ( attackClassesList.includes(cn) ) {
            GM_log("We are under Attack!!!");
            play();
            clearTimeout(t);
            break;
        }
    }
    t = setTimeout(attack, 4000);
}

function play() {
    //var audio = new Audio(alarmMp3);
    //audio.play();
    audio.controls = true;
    document.body.appendChild(audio);
    audio.play();
}

function randomNumber(min, max) {
    return Math.random() * (max - min) + min;
}

