function loadValue( name, defaultValue)
{
    return GM_getValue( getCurrentServer() + '_' + name, defaultValue);
}

function saveValue( name, value)
{
    GM_setValue( getCurrentServer() + '_' + name, value);
}

function getCurrentServer() {
    var serverr = window.location.hostname;
    return serverr;
}

function createFloatWindow()
{
    var taaFloatWindow = document.createElement("div");
    taaFloatWindow.style.cssText = 'width:200px; top:'+xPos+';left:'+yPos+';position: absolute;opacity:0.90;z-index:100;border:1px;border-style:solid;border-radius: 5px 5px 0 0;';
    taaFloatWindow.id = "TaaFloatWindow";

    var taaBlock = document.createElement("div");
    taaBlock.style.cssText = 'border-radius: 5px 5px 0 0;';
    taaBlock.id = "TAABlock";


    var taaFloatWindowHeader = document.createElement("div");
    taaFloatWindowHeader.style.cssText = 'width:100%;height:10px;cursor:move;background-color:#DDDDFF;border-radius: 5px 5px 0 0;';
    taaFloatWindowHeader.id = "TaaFloatWindowHeader";

    var taaHeaderButton = document.createElement("div");
    taaHeaderButton.style.cssText = 'float:right;cursor:pointer;padding:3px;';
    taaHeaderButton.id = "TaaHeaderButton";

    taaFloatWindow.appendChild(taaHeaderButton);
    taaFloatWindow.appendChild(taaBlock);

    var taaBlockBody = document.createElement("div");
    taaBlockBody.style.cssText = 'background-color:white;width:100%;';
    taaBlockBody.id = "TaaBlockBody";
    //taaBlockBody.innerHTML = '<span>' + getCurrentServer() + '</span>';

    taaBlock.appendChild(taaFloatWindowHeader);
    taaBlock.appendChild(taaBlockBody);

    document.body.appendChild(taaFloatWindow);

    dragElement(document.getElementById("TaaFloatWindow"));

    return taaBlockBody;
}

// Draggable example from: https://www.w3schools.com/howto/howto_js_draggable.asp
function dragElement(elmnt) {
    var pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
    if (document.getElementById(elmnt.id + "header")) {
        // if present, the header is where you move the DIV from:
        document.getElementById(elmnt.id + "header").onmousedown = dragMouseDown;
    } else {
        // otherwise, move the DIV from anywhere inside the DIV:
        elmnt.onmousedown = dragMouseDown;
    }

    function dragMouseDown(e) {
        e = e || window.event;
        e.preventDefault();
        // get the mouse cursor position at startup:
        pos3 = e.clientX;
        pos4 = e.clientY;
        document.onmouseup = closeDragElement;
        // call a function whenever the cursor moves:
        document.onmousemove = elementDrag;
    }

    function elementDrag(e) {
        e = e || window.event;
        e.preventDefault();
        // calculate the new cursor position:
        pos1 = pos3 - e.clientX;
        pos2 = pos4 - e.clientY;
        pos3 = e.clientX;
        pos4 = e.clientY;
        // set the element's new position:
        elmnt.style.top = (elmnt.offsetTop - pos2) + "px";
        elmnt.style.left = (elmnt.offsetLeft - pos1) + "px";
    }

    function closeDragElement() {
        // stop moving when mouse button is released:
        saveValue("xPos", elmnt.style.top);
        saveValue("yPos", elmnt.style.left);
        document.onmouseup = null;
        document.onmousemove = null;
    }
}
